// One hot encoding function
std::vector<double> oneHotEncode(int index, int size) {
    std::vector<double> one_hot(size, 0.0);
    one_hot[index] = 1.0;
    return one_hot;
}

std::vector<std::vector<double>> arrayToVector(const double inputArray, int row_num, int col_num) {
    std::vector<std::vector<double>> outputVector;
    for (int i = 0; i < row_num; i++) {
        std::vector<double> row;
        for (int j = 0; j < col_num; j++) {
            row.push_back(inputArray[i][j]);
        }
        outputVector.push_back(row);
    }
    return outputVector;
}

